page.code:
    
        variable min 0
        variable max 1000000
        variable pattern ""
        variable regex ""
        variable value ""
        
        proc OnNumber { control } {
            variable min
            variable max 
            variable pattern 
            variable first_key 
            variable cursor
            if { $first_key } {
                clear
                set first_key 0
            }
            set text [ui get result.text]
            if { "$pattern" == "" } {
                if { [string length $text] <= 10 } {
                    set text "$text$control"
                }
            } else {
                if { $cursor >= [string length $pattern] } return
                set text [string replace $text $cursor $cursor $control]
                incr cursor
                set cursor [string first "#" $pattern $cursor]
                if { $cursor < 0 } { 
                    set cursor [string length $pattern] 
                }
            }
            ui set result.text $text
            validate
        }

        proc OnBackspace { control } {
            variable pattern 
            variable first_key
            set first_key 0
            if { "$pattern" == "" } {
                num_backspace
            } else {
                pattern_backspace
            }
        }
        
        proc num_backspace {} {
            variable min 
            variable max 
            variable cursor
            set text [string range [ui get result.text] 0 end-1]
            if { [string length $text] } {
                if { $text < $min } return
                if { $text > $max } return
            }
            incr cursor -1
            ui set result.text $text
            validate
        }

        proc pattern_backspace {} {
            variable pattern 
            variable cursor
            incr cursor -1
            set cursor [string last "#" $pattern $cursor]
            if { $cursor < 0 } {return }
            ui set result.text [string replace [ui get result.text] $cursor $cursor "_"]
            validate
        }

        proc validate {} {
            variable pattern
            if { "$pattern" == "" } {
                return [num_validate]
            } else {
                return [pattern_validate]
            }
        }
        
        proc num_validate {} {
            variable min 
            variable max
            set text [ui get result.text]
            set valid 1
            if { ![string length $text] } {
                set valid 0
            }
            if { $text < $min } { set valid 0 }
            if { $text > $max } { set valid 0 }
            if { !$valid } {
                ui set tOk.color 0x808080
            } else {
                ui set tOk.color 0x000000
            }
            return $valid
        }
        
        proc pattern_validate {} {
            variable cursor 
            variable pattern 
            variable regex
            set valid 1
            if { $cursor < [string length $pattern] } {
                set valid 0
            } elseif { [string length $regex] } {
                set valid [regexp "^${regex}\$" [ui get result.text]]
            }
            if { !$valid } {
                ui set tOk.color 0x808080
            } else {
                ui set tOk.color 0x000000
            }
            return $valid
        }
        
        proc ResetControls {} {
            ui set title.text "Eingabe"
            ui set subtitle.text ""
            variable min 0
            variable max 1000000
            variable pattern ""
            variable regex ""
        }
        
        proc OnOk { control } {
            variable min
            if { ![validate] } {
                return
            }
            ResetControls
            variable value [ui get result.text]
            ui back
        }

        proc OnCancel { control } {
            ResetControls
            ui back
        }
        
        proc clear { } {
            variable pattern 
            variable cursor
            ui set result.text [string map {# _} $pattern]
            if { "$pattern" == "" } { 
                set cursor 0
            } else {
                set cursor [string first "#" $pattern]
            }
        }

        proc OnPageLoad { previousPage reason } {
            variable min 
            variable max 
            variable pattern 
            variable first_key 
            variable value 
            variable regex
            variable cursor

            ui set result.text $value
            set cursor [string length $value]
            set first_key 1
            if { !$cursor } {
                clear
            }
            validate
        }
